<?phpclass Dbconnect{    private $host = 'localhost';    private $username = 'root';    private  $password = '';    private $DbName = 'capstone_ecommerce';    public $conn;    public function Connect()    {        $this->conn = new \mysqli($this->host,$this->username,$this->password,$this->DbName);        if ($this->conn->connect_error){            die("Connection problem");        }        return $this->conn;    }}