n 3 -->        <div class="col-lg-4 col-md-6 mb-4">            <div class="icon-container mb-3">                <img src="./Assets/images/Unauth_HP_illo3.svg" alt="Save with Grubhub+">            </div>            <div class="icon-title">Save with Us</div>            <div class="icon-text">                Join us and get exclusive offers, discounts, and much more.            </div>        </div>    </div>    <!-- Highlight Section with Text and Image -->    <div class="row align-items-center mt-5">        <div class="col-lg-6">            <div class="highlight-section">                Pickup or delivery from restaurants near you            </div>            <p>                Explore restaurants that deliver near you, or try yummy takeout fare. With a place for every taste, it’s easy to find the food you crave and order online Fast and fresh meals delivered to your door.            </p>        </div>        <div class="col-lg-6">            <img  src="./Assets/images/logo.png" class="img-fluid col-5" alt="People enjoying food">        </div>    </div></div><!-- Modal for Register--><div class="modal fade" id="RegisterModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">    <form id="regiterForm" action="./src/controller/AuthController.php" method="post" class="modal-dialog modal-dialog-centered modal-lg">        <div class="modal-content">            <div class="modal-header">                <h6 class="modal-title text-muted" id="exampleModalLabel">Sign Up</h6>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body">                <div class="form-floating mb-3">                    <input name="username" type="email" class="form-control" id="floatingInput1" placeholder="name@example.com">                    <label for="floatingInput">Email address</label>                </div>                <div class="form-floating mb-3">                    <input name="passwords" type="password" class="form-control" id="floatingInput2">                    <label for="floatingInput">Password</label>                </div>                <div class="form-floating mb-3">                    <input name="Name" type="text" class="form-control" id="floatingInput3">                    <label for="floatingInput">full Name</label>                </div>                 <div class="form-floating mb-3">                    <input name="Address" type="text" class="form-control" id="floatingInput4">                    <label for="floatingInput">Address</label>                </div>                <div class="form-floating mb-3">                    <input name="phone" type="number" class="form-control" id="floatingInput5">                    <label for="floatingInput">Active Phone Number</label>                </div>            </div>            <div class="modal-footer">                <button type="submit" class="btn btn-primary">Sign Up</button>            </div>        </div>    </form></div><!-- Modal for Login--><div class="modal fade" id="LoginModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">    <form id="LoginForm" action="./src/controller/AuthController.php" method="post" class="modal-dialog modal-dialog-centered modal-sm">        <div class="modal-content">            <div class="modal-header">                <h6 class="modal-title text-muted" id="exampleModalLabel">Sign In</h6>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body">                <div class="form-floating mb-3">                    <input name="username" type="email" class="form-control" id="floatingInput6" placeholder="name@example.com">                    <label for="floatingInput">Email address</label>                </div>                <div class="form-floating mb-3">                    <input name="passwords" type="password" class="form-control" id="floatingInput7">                    <label for="floatingInput">Password</label>                </div>            </div>            <div class="modal-footer">                <button type="submit" class="btn btn-primary">Sign In</button>            </div>        </div>    </form></div><footer class="bg-dark text-white pt-4">    <div class="container">        <div class="row">            <!-- About Section -->            <div class="col-lg-4 col-md-6 mb-4">                <h5>About Us</h5>                <p>                    We are dedicated to providing you with the best products, ensuring quality and customer satisfaction.                </p>            </div>            <!-- Links Section -->            <div class="col-lg-4 col-md-6 mb-4">                <h5>Quick Links</h5>                <ul class="list-unstyled">                    <li><a href="#" class="text-white">Home</a></li>                    <li><a href="#" class="text-white">Shop</a></li>                    <li><a href="#" class="text-white">Contact Us</a></li>                    <li><a href="#" class="text-white">Privacy Policy</a></li>                </ul>            </div>            <!-- Contact Section -->            <div class="col-lg-4 col-md-6 mb-4">                <h5>Contact Us</h5>                <ul class="list-unstyled">                    <li><i class="bi bi-telephone-fill"></i> +63 123 456 789</li>                    <li><i class="bi bi-envelope-fill"></i> support@example.com</li>                    <li><i class="bi bi-geo-alt-fill"></i> 123 Main Street, Manila, PH</li>                </ul>            </div>        </div>        <!-- Social Media Icons -->        <div class="row justify-content-center">            <div class="col-auto">                <a href="#" class="text-white me-3"><i class="bi bi-facebook"></i></a>                <a href="#" class="text-white me-3"><i class="bi bi-twitter"></i></a>                <a href="#" class="text-white me-3"><i class="bi bi-instagram"></i></a>                <a href="#" class="text-white"><i class="bi bi-linkedin"></i></a>            </div>        </div>        <!-- Copyright -->        <div class="text-center mt-3">            <p class="mb-0">&copy; 2024 Your Company. All Rights Reserved.</p>        </div>    </div></footer><script>    $(document).ready(function (){        $(document).on('submit','#regiterForm',function (event){            event.preventDefault()            const formData = new FormData(this)            formData.append('action','register')            $.ajax({                url: './src/controller/AuthController.php',                type: 'post',                data: formData,                contentType: false,                processData: false,                dataType: 'json',                success: function (res){                   if (res.error){                       Swal.fire({                           text: res.error.errorMessage,  // Display the error message                           icon: 'warning',                           toast: true,                           target: 'body',                           position: 'top-end',                           showConfirmButton: false,                           timer: 2000,  // Auto-close the alert after 2 seconds                           timerProgressBar: true // Show the progress bar for the timer                       });                   }                   if (res.success === true){                       Swal.fire({                           text: res.message,  // Display the error message                           toast: true,                           target: 'body',                           position: 'top-end',                           showConfirmButton: false,                           timer: 2000,  // Auto-close the alert after 2 seconds                           timerProgressBar: true  // Show the progress bar for the timer                       });                       $('#RegisterModal').modal('hide')                        setTimeout(() =>{window.location.reload()},2000)                   }                }            })        })        $(document).on('submit','#LoginForm',function (event){            event.preventDefault()           const formData = new FormData(this)            formData.append('action','login')                $.ajax({                    url: './src/controller/AuthController.php',                    type: 'post',                    data: formData,                    contentType: false,                    processData: false,                    dataType: 'json',                    success: function (res){                        console.log(res)                        if (res.error){                            Swal.fire({                                text: res.error.errorMessage,  // Display the error message                                icon: 'warning',                                toast: true,                                target: 'body',                                position: 'top-end',                                showConfirmButton: false,                                timer: 2000,  // Auto-close the alert after 2 seconds                                timerProgressBar: true // Show the progress bar for the timer                            });                        }else {                            Swal.fire({                                text: res.message,  // Display the error message                                icon: 'error',                                toast: true,                                target: 'body',                                position: 'top-end',                                showConfirmButton: false,                                timer: 2000,  // Auto-close the alert after 2 seconds                                timerProgressBar: true // Show the progress bar for the timer                            });                        }                        if (res.success === true){                            Swal.fire({                                text: res.message,  // Display the error message                                icon: 'success',                                toast: true,                                target: 'body',                                position: 'top-end',                                showConfirmButton: false,                                timer: 2000,  // Auto-close the alert after 2 seconds                                timerProgressBar: true // Show the progress bar for the timer                            });                            setTimeout(() =>{window.location.href = './src/views/store.php'},2000)                        }                    }                })        })    })</script></body></html>