<?phpnamespace controller;use model\AuthModel;include_once '../model/AuthModel.php';class AuthController{ public function registerUsers() {     $username = $_POST['username'];     $password = $_POST['passwords'];     $Name = $_POST['Name'];     $Address = $_POST['Address'];     $phone = $_POST['phone'];     $model = new AuthModel();     // check if the username is existing     $IsExistingUser = new AuthModel();     $data = [];     $error = [];     if ($IsExistingUser->getUserName($username)) {  // Check by username, not password         $error['errorMessage'] = 'Username already exists';     }     if (empty($username)){         $error['errorMessage'] = 'user name is required';     }     if (empty($password)){         $error['errorMessage'] = 'password is required';     }     if (empty($Name)){         $error['errorMessage'] = 'name is required';     }     if (empty($Address)){         $error['errorMessage'] = 'address name is required';     }     if (empty($phone)){         $error['errorMessage'] = 'phone number is required';     }     if (!empty($error)){         $data['success'] = false;         $data['error'] = $error;         echo json_encode($data);     }else{         // hash the password         $passwordHashed = password_hash($password,PASSWORD_BCRYPT);         $model->registerUser($username,$passwordHashed,$Address,$phone,$Name);     } } public function Login() {     $username = $_POST['username'];     $password = $_POST['passwords'];     $model = new AuthModel();     $data = [];     $error = [];     if (empty($username)){         $error['errorMessage'] = 'username is required';     }     if (empty($password)){         $error['errorMessage'] = 'password is required';     }     if (!empty($error)){         $data['success'] = false;         $data['error'] = $error;         echo json_encode($data);     }else{         $model->Login($username,$password);     } } public function adminLogin() {     $userName = $_POST['username'];     $password = $_POST['password'];     if (empty($password) && empty($userName)) {         echo json_encode(['success' => false, 'message' => 'please provide username or password']);     }else{         $admin = new AuthModel();         $admin->adminLogin($userName,$password);     } }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && $_POST['action']){    $Auth = new AuthController();    switch ($_POST['action']){        case 'register':            $Auth->registerUsers();            break;        case 'login':            $Auth->Login();            break;        case 'admin':            $Auth->adminLogin();            break;    }}