<?phpnamespace controller;use model\inventoryModel;include_once '../model/inventoryModel.php';class itemController{  public function addItem()  {      $item = $_POST['Item'];      $Quantity = $_POST['Quantity'];      $price = $_POST['price'];      $model = new inventoryModel();      $data = [];      $error = [];      if (empty($item)){          $error['errorMessage'] = 'item name is required';      }       if (empty($Quantity)){          $error['errorMessage'] = 'Quantity is required';      }       if (empty($price)){          $error['errorMessage'] = 'price is required';      }       if (!empty($error)){           $data['success'] = false;           $data['error'] = $error;           echo json_encode($data);       }else{           $model->storeItem($item,$price,$Quantity);       }  }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    $controller = new itemController();    switch ($_POST['action']){        case 'addItem':            $controller->addItem();            break;    }}