<?phpnamespace model;include_once '../../config/Dbconnect.php';class categoryModel extends \Dbconnect{  public function createCategory($categoryName)  {     $query = "INSERT INTO category(categoryName)VALUES(?)";     $stmt = $this->Connect()->prepare($query);     $stmt->bind_param('s',$categoryName);     if ($stmt->execute()){         echo json_encode(['success' => true, 'message' => 'successfully created new category']);     }else{         echo json_encode(['success' => false, 'error' => $stmt->error]);     }  }  public function deleteCategory($categoryId)  {     $query = "DELETE FROM category WHERE categoryId = ?";     $stmt = $this->Connect()->prepare($query);     $stmt->bind_param('i',$categoryId);      if ($stmt->execute()){          echo json_encode(['success' => true, 'message' => 'category is successfully deleted']);      }else{          echo json_encode(['success' => false, 'error' => $stmt->error]);      }  }  public function saveCategory($categoryName,$categoryId)  {      $query = "UPDATE category SET categoryName = ? WHERE categoryId = ?";      $stmt = $this->Connect()->prepare($query);      $stmt->bind_param('si',$categoryName,$categoryId);      if ($stmt->execute()){          echo json_encode(['success' => true, 'message' => 'category is successfully updated']);      }else{          echo json_encode(['success' => false, 'error' => $stmt->error]);      }  }    public function updateCategory($categoryId)    {        $query = "SELECT * FROM category WHERE categoryId = ?";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('i',$categoryId);         $stmt->execute();         $result = $stmt->get_result();         if ($result->num_rows > 0){             $dataRow = [];             while ($row = $result->fetch_assoc()){                 $dataRow[] = $row;             }             return $dataRow;         }         return  null;    }    public function displayCategory()    {        $result = $this->Connect()->query("SELECT * FROM category");        if ($result->num_rows > 0){            $dataRow = [];            while ($row = $result->fetch_assoc()){                $dataRow[] = $row;            }            return $dataRow;        }        return null;    }}