<?phpnamespace model;include_once '../../config/Dbconnect.php';class orderModel extends \Dbconnect{    public function createOrders($productId, $productName, $Total, $price, $costumerName, $Address, $Email, $phone, $quantity)    {        $query = "INSERT INTO orders(productid, productname, orderamount, unitprice, costumername, address, email, phonenumber, quantity)                   VALUES (?,?,?,?,?,?,?,?,?)";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('isdissssi', $productId, $productName, $Total, $price, $costumerName, $Address, $Email, $phone, $quantity);        if (!$stmt->execute()) {            // Return false if there's an error            return ['success' => false, 'error' => $stmt->error];        }        // Return true on success        return ['success' => true];    }    public function displayOrders()    {        $result = $this->Connect()->query("SELECT OrderId,ProductName,UnitPrice,quantity,OrderAmount,CostumerName,Status FROM orders");        if ($result->num_rows >0){          $dataRow = [];          while ($row = $result->fetch_assoc()){              $dataRow[] = $row;          }          return $dataRow;        }        return  null;    }}