<?phpnamespace model;include_once '../../config/Dbconnect.php';class productModel extends \Dbconnect{   public function addProducts($productName,$price,$image)   {        $query = "INSERT INTO product(ProductName, Price, image)VALUES (?,?,?)";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('sis',$productName,$price,$image);        if ($stmt->execute()){            echo json_encode(['success' => true, 'message' => 'Successfully Added New Product']);        }else{            echo json_encode(['success' => false, 'error' => $stmt->error]);        }   }   public function displayProducts()   {      $result = $this->Connect()->query("SELECT * FROM product WHERE archiveStatus = 'Active'");      if ($result->num_rows > 0){          $dataRow =[];          while ($row = $result->fetch_assoc()){              $dataRow[] = $row;          }          return $dataRow;      }      return null;   }}