l Modal --><div class="modal fade" id="categoryModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">    <form id="formItem" action="../controller/itemController.php" method="post" class="modal-dialog modal-dialog-centered">        <div class="modal-content">            <div class="modal-header">                <h1 class="modal-title fs-6 text-muted" id="exampleModalLabel">Details</h1>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body">                <div class="form-floating mb-3">                    <input name="Item" type="text" class="form-control" id="floatingInput">                    <label for="floatingInput">Item</label>                </div>                <div class="form-floating mb-3">                    <input name="Quantity" type="number" class="form-control" id="floatingInput">                    <label for="floatingInput">Quantity</label>                </div>                <div class="form-floating mb-3">                    <input name="price" type="number" class="form-control" id="floatingInput">                    <label for="floatingInput">Price/unit</label>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>                <button type="submit" class="btn btn-primary">Add Now</button>            </div>        </div>    </form>    <?php    ?></div><script>    let table = new DataTable('#example',{        scrollCollapse: true,        scrollY: '50vh',        destroy: true,        paging: true,        searching: true,        ordering: true    });</script><script>    $(document).ready(function (){        $(document).on('submit','#formItem',function (event){            event.preventDefault();            const formData = new FormData(this)            formData.append('action','addItem')            $.ajax({                url: '../controller/itemController.php',                type: 'post',                data: formData,                processData: false,                contentType: false,                dataType: 'json',                success: function (res){                    if (res.error){                        Swal.fire({                            text: res.error.errorMessage,  // Display the error message                            confirmButtonColor: '#4361ee',                            icon: 'error',                            toast: true,                            target: 'body',                            position: 'top-end',                            showConfirmButton: false,                            timer: 2000,  // Auto-close the alert after 2 seconds                            timerProgressBar: true,  // Show the progress bar for the timer                        });                    }                    if (res.success === true){                        Swal.fire({                            icon: "success",                            text: res.message,                            showConfirmButton: false,                            timer: 1500                        });                        setTimeout(() =>{window.location.reload()},2000)                    }                }            })        })    })</script></body></html>