Auto-close the alert after 2 seconds                                timerProgressBar: true,  // Show the progress bar for the timer                            });                        }                        if (res.success === true){                            Swal.fire({                                icon: "success",                                text: res.message,                                showConfirmButton: false,                                timer: 1500                            });                            setTimeout(() =>{window.location.reload()},2000)                        }                    }                })        })        $(document).on('click','#btn_update',function (e){            $.ajax({                url:'../controller/productController.php',                type: 'post',                data: {                    id: e.target.value,                    action: 'update'                },                success: function (res){                    $('body').append(res)                    $(`#modalUpdate_${e.target.value}`).modal('show')                }            })        })        $(document).on('submit','#formProductUpdate',function (event){            event.preventDefault()            const formData = new FormData(this)            formData.append('action','Save')            $.ajax({                url:'../controller/productController.php',                type: 'post',                contentType: false,                processData: false,                data:formData,                dataType: 'json',                success: function (res){                    switch (res.success){                        case false:                            Swal.fire({                                text: res.message,  // Display the error message                                confirmButtonColor: '#4361ee',                                icon: 'error',                                toast: true,                                target: 'body',                                position: 'top-end',                                showConfirmButton: false,                                timer: 2000,  // Auto-close the alert after 2 seconds                                timerProgressBar: true,  // Show the progress bar for the timer                            });                            break;                        case true:                            Swal.fire({                                icon: "success",                                text: res.message,                                showConfirmButton: false,                                timer: 1500                            });                            setTimeout(() =>{window.location.reload()},2000)                            break;                    }                }            })        })        $(document).on('click','#btn_archive',function (e){            Swal.fire({                title: "Are you sure?",                text: "You won't be able to revert this!",                icon: "warning",                showCancelButton: true,                confirmButtonColor: "#3085d6",                cancelButtonColor: "#d33",                confirmButtonText: "Yes, delete it!"            }).then((result) => {                if (result.isConfirmed) {                   // ajax                    $.ajax({                        url:'../controller/productController.php',                        type: 'post',                        data: {                            id: e.target.value,                            action: 'archive'                        },                        success: function (res){                            Swal.fire({                                icon: "success",                                text: res.message,                                showConfirmButton: false,                                timer: 1500                            });                            setTimeout(() =>{window.location.reload()},2000)                        }                    })                }            });        })    })</script></body></html>