<?phpsession_start();if (isset($_SESSION['rider'])){    header('Location: ./riderView.php');}?><!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <title>Rider View</title>    <link rel="stylesheet" href="../../css/bootstrap.min.css">    <link rel="stylesheet" href="../../style.css">    <script src="../../js/bootstrap.bundle.min.js"></script>    <link rel="stylesheet" type="text/css" href="https://cdn.datatables.net/1.11.5/css/jquery.dataTables.css">    <!-- Include jQuery -->    <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>    <!-- Include DataTables JS -->    <script type="text/javascript" charset="utf8" src="https://cdn.datatables.net/1.11.5/js/jquery.dataTables.js"></script>    <!--    ajax cdn-->    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <!--    cdn sweet alert-->    <script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script></head><style>    body{        width: 100%;        height: 100vh;        display: flex;        justify-content: center;        align-items: center;        background-color: #000000;    }</style><body><div  class="container d-flex flex-column  justify-content-center align-items-center">    <form id="loginRiderForm" style="background-color: #0B192C" action="../controller/AuthController.php" method="post" class="container rounded-5 p-5 d-flex flex-column col-lg-5 p-3">         <div class="d-flex flex-column justify-content-center align-items-center">             <img  class="img-fluid col-lg-3 col-md-3 mb-3 " src="../../Assets/images/logo.png" alt="logo">             <h5 class="text-light">Granny's Rider</h5>         </div>        <div class="form-floating mb-3">            <input name="username" type="email" class="form-control"  placeholder="name@example.com">            <label for="floatingInput">Email address</label>        </div>        <div class="form-floating mb-3">            <input name="password" type="password" class="form-control"  placeholder="Password">            <label for="floatingInput">Password</label>        </div>        <button type="submit" class="btn btn-primary">Login</button>    </form></div><script>    $(document).ready(function (){        $(document).on('submit','#loginRiderForm',function (event){            event.preventDefault();            const formData = new FormData(this)            formData.append('action','riderLogin')            $.ajax({                url: '../controller/AuthController.php',                type: 'post',                data: formData,                contentType: false,                processData: false,                dataType: 'json',                success: function (res){                    if (res.success === true){                        Swal.fire({                            text: res.message,  // Display the error message                            confirmButtonColor: '#4361ee',                            icon: 'success',                            toast: true,                            target: 'body',                            position: 'top-end',                            showConfirmButton: false,                            timer: 2000,  // Auto-close the alert after 2 seconds                            timerProgressBar: true,  // Show the progress bar for the timer                        });                        setTimeout(() => {window.location.href = './riderView.php'},2000)                    }else {                        Swal.fire({                            text: res.message,  // Display the error message                            confirmButtonColor: '#4361ee',                            icon: 'error',                            toast: true,                            target: 'body',                            position: 'top-end',                            showConfirmButton: false,                            timer: 2000,  // Auto-close the alert after 2 seconds                            timerProgressBar: true,  // Show the progress bar for the timer                        });                    }                }            })        })    })</script></body></html>