p';             $rider = new  \controller\riderConroller();             $rider->showAllRider();        ?>        </tbody>    </table></section><div class="modal fade" id="modal_adding_Product" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">    <div id="createRider" class="modal-dialog modal-dialog-centered">        <div class="modal-content">            <div class="modal-header">                <h1 class="modal-title fs-6 text-muted text-muted" id="exampleModalLabel">Rider Credential</h1>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <form id="formRider" action="../controller/riderConroller.php" method="post"  class="modal-body d-flex flex-column gap-2">                <div class="form-floating mb-3">                    <input name="username" type="email" class="form-control" id="floatingInput" placeholder="username">                    <label for="floatingInput">Email address</label>                </div>                <div class="form-floating">                    <input name="password" type="password" class="form-control" id="floatingPassword" placeholder="Password">                    <label for="floatingPassword">Password</label>                </div>                <div class="modal-footer">                    <button type="button" class="btn btn-outline-danger" data-bs-dismiss="modal">Close</button>                    <button style="background-color:  #4361ee" type="submit" class="btn text-light">                        <i class="fa-solid fa-plus"></i>                        Create                    </button>                </div>            </form>        </div>    </div></div><script>    let table = new DataTable('#example',{        scrollCollapse: true,        scrollY: '50vh',        destroy: true,        paging: true,        searching: true,        ordering: true    });</script><script>    $(document).ready(function (){        $(document).on('submit','#formRider',function (event){            event.preventDefault();            const formData = new FormData(this)            formData.append('action','storeRider')            $.ajax({                url: '../controller/riderConroller.php',                type: 'post',                data: formData,                contentType: false,                processData: false,                dataType: 'json',                success: function (res){                    if (res.success === true){                        Swal.fire({                            text: res.message,                            icon: "success"                        });                        setTimeout(() => {window.location.reload()},2000)                    }else {                        Swal.fire({                            text: res.message,                            icon: "info"                        });                    }                }            })        })        $(document).on('click','#ntn_updateRider',function (e){            $.ajax({                url: '../controller/riderConroller.php',                type: 'post',                data: {id: e.target.value, action: 'showRiderBaseOnId'},                success: function (data){                    $('body').append(data)                    $(`#updateModalRider_${e.target.value}`).modal('show')                }            })        })        $(document).on('submit','#formRiderUpdate',function (event){            event.preventDefault();            const formData = new FormData(this)            formData.append('action','SaveRiderInfo')            $.ajax({                url: '../controller/riderConroller.php',                type: 'post',                data: formData,                contentType: false,                processData: false,                dataType: 'json',                success: function (res){                    if (res.success === true){                        Swal.fire({                            text: res.message,                            icon: "success"                        });                        setTimeout(() => {window.location.reload()},2000)                    }else {                        Swal.fire({                            text: res.message,                            icon: "error"                        });                    }                }            })        })        $(document).on('click','#riderRemove',function (e){            Swal.fire({                title: "Are you sure?",                text: "You won't be able to revert this!",                icon: "warning",                showCancelButton: true,                confirmButtonColor: "#3085d6",                cancelButtonColor: "#d33",                confirmButtonText: "Yes, delete it!"            }).then((result) => {                if (result.isConfirmed) {                    $.ajax({                        url: '../controller/riderConroller.php',                        type: 'post',                        data: {id: e.target.value,action: 'removeRider'},                        dataType: 'json',                        success: function (res){                            console.log(res)                            if (res.success === true){                                Swal.fire({                                    text: res.message,                                    icon: "success"                                });                                setTimeout(() => {window.location.reload()},2000)                            }else {                                Swal.fire({                                    text: res.message,                                    icon: "error"                                });                            }                        }                    })                }            });        })    })</script></body></html>