<script>    let table = new DataTable('#example',{        scrollCollapse: true,        scrollY: '50vh',        destroy: true,        paging: true,        searching: true,        ordering: true    });</script><script>    $(document).ready(function (){        $(document).on('click','#btn_view',function (e){            $.ajax({                url:'../controller/riderConroller.php',                type: 'post',                data:{                    action: 'order',                    email: e.target.value                },                success: function (data) {                    if ($('#DetailsModal').length === 0) {                        $('body').append(data);                    } else {                        $('#DetailsModal').replaceWith(data)                    }                    $('#DetailsModal').modal('show')                }            })        })        $(document).on('submit','#formDrop',function (event){            event.preventDefault();            const formData = new FormData(this)            formData.append('action','drop')            $.ajax({                url:'../controller/riderConroller.php',                type: 'post',                data: formData,                dataType: 'json',                contentType: false,                processData: false,                success: function (res){                    console.log(res)                    switch (res.success){                        case false:                            Swal.fire({                                text: res.message,  // Display the error message                                confirmButtonColor: '#4361ee',                                icon: 'error',                                toast: true,                                target: 'body',                                position: 'top-end',                                showConfirmButton: false,                                timer: 2000,  // Auto-close the alert after 2 seconds                                timerProgressBar: true,  // Show the progress bar for the timer                            });                            break;                        case true:                            Swal.fire({                                icon: "success",                                text: res.message,                                showConfirmButton: false,                                timer: 1500                            });                            setTimeout(() =>{window.location.reload()},2000)                            break;                    }                }            })        })    })</script></body></html>